mapper.insert:
insert into code(
	id
	, nm
	, value
	, parent_id
	, description
	, create_date
	, where_used
) values (



			(select to_char( now() , 'YYYYMMDDHH24MIMS'))


			#{id}


	, #{nm}
	, #{value}


			#{parentId}


			, lpad(''::text, 17, '0'::text)


	, #{description}
	, current_timestamp

		 #{item}

)
